import React, { useState } from "react";
import {
  GoogleMap,
  Marker,
  LoadScript,
  StandaloneSearchBox,
  DirectionsService,
  DirectionsRenderer,
} from "@react-google-maps/api";
import { REACT_APP_GOOGLE_API_KEY } from "../App";
import "./MapPage.css";

export interface MapPageProps { }

const MapPage = () => {
  const [map, setMap] = useState<google.maps.Map>();
  const [searchBoxA, setSearchBoxA] = useState<google.maps.places.SearchBox>();
  const [searchBoxB, setSearchBoxB] = useState<google.maps.places.SearchBox>();
  const [pointA, setPointA] = useState<google.maps.LatLngLiteral>();
  const [pointB, setPointB] = useState<google.maps.LatLngLiteral>();
  const [origin, setOrigin] = useState<google.maps.LatLngLiteral | null>(null);
  const [destination, setDestination] = useState<google.maps.LatLngLiteral | null>(null);
  const [response, setResponse] = useState<google.maps.DistanceMatrixResponse | null>(null);

  const position = {
    lat: -20.275986400323127,
    lng: -50.541355008625324,
  };

  const onMapLoad = (map: google.maps.Map) => {
    setMap(map);
  };

  const onLoadA = (ref: google.maps.places.SearchBox) => {
    setSearchBoxA(ref);
  };

  const onLoadB = (ref: google.maps.places.SearchBox) => {
    setSearchBoxB(ref);
  };

  const onPlacesChangedA = () => {
    const places = searchBoxA!.getPlaces();
    if (places) {
      const location = places[0].geometry?.location;
      setPointA(location.toJSON());
      setOrigin(location.toJSON());
    }
  };

  const onPlacesChangedB = () => {
    const places = searchBoxB!.getPlaces();
    if (places) {
      const location = places[0].geometry?.location;
      setPointB(location.toJSON());
      setDestination(location.toJSON());
    }
  };

  const traceRoute = () => {
    if (pointA && pointB) {
      setOrigin(pointA);
      setDestination(pointB);
    }
  };

  const directionsServiceOptions = {
    origin: origin,
    destination: destination,
    travelMode: "DRIVING" as google.maps.TravelMode,
  };

  const directionsCallback = (res: google.maps.DirectionsResult | null) => {
    if (res && res.status === "OK") {
      setResponse(res);
    }
  };

  return (
    <div className="map-page">
      <LoadScript
        googleMapsApiKey={REACT_APP_GOOGLE_API_KEY}
        libraries={["places"]}
      >
        <GoogleMap
          onLoad={onMapLoad}
          mapContainerStyle={{ width: "100%", height: "90vh" }} // Ajuste para a altura do mapa
          center={position}
          zoom={15}
        >
          {!response && pointA && <Marker position={pointA} />}
          {!response && pointB && <Marker position={pointB} />}

          {origin && destination && (
            <DirectionsService
              options={directionsServiceOptions}
              callback={directionsCallback}
            />
          )}

          {response && (
            <DirectionsRenderer
              options={{
                directions: response,
              }}
            />
          )}
        </GoogleMap>
      </LoadScript>
      {/* Seção de endereços e botão de traçar rota movidos para fora do GoogleMap */}
      <div className="address">
        <StandaloneSearchBox
          onLoad={onLoadA}
          onPlacesChanged={onPlacesChangedA}
        >
          <input
            className="addressField"
            type="text"
            placeholder="Digite o endereço inicial"
          />
        </StandaloneSearchBox>
        <StandaloneSearchBox
          onLoad={onLoadB}
          onPlacesChanged={onPlacesChangedB}
        >
          <input
            className="addressField"
            type="text"
            placeholder="Digite o endereço final"
          />
        </StandaloneSearchBox>
        <button className="routeButton" onClick={traceRoute}>Traçar rota</button>
      </div>
    </div>
  );
};

export default MapPage;
